const addTime = (item) => {
  const existingItem = toWatch.find(i => i.name === item.name)
  if (existingItem) {
    setToWatch(
      toWatch.map(oldItem =>
        oldItem === existingItem
          ? { ...oldItem, count: existingItem.count + 1 }
          : { ...oldItem }
      )
    )
