import './App.css';
import { useState } from "react";
import { movieData } from "./assets/tiles.js"
import { Time } from './Time';
import { Filter } from './Filter';
import { FormControl } from '@mui/material';

function App() {

  //TODO: UPDATE THIS 
  // movieData.forEach((item) => {
  //   item.image = process.env.PUBLIC_URL + "/" + item.image;
  // });

  const [toWatch, setToWatch] = useState([])
  const [type, setType] = useState("All")

  const selectFilterType = eventKey => {
    setType(eventKey);
  }    

  const matchesFilterType = item => {
    if(type === "All") { 
      return true
    } else if (item.genre.includes(type)) {
      return true
    } else {
      return false
    }
  }

  const items = toWatch.map(item =>
    <li key={item.id}>
      {item.name} : {item.time} minutes
    </li>

)

const totalTime =  toWatch.reduce((partialSum, item) => partialSum + item.time, 0)  

const addTime = (item) => {
  const existingItem = toWatch.find(i => i.name === item.name)
  if (existingItem) {
    setToWatch(
      toWatch.map(oldItem =>
        oldItem === existingItem
          ? { ...oldItem}
          : { ...oldItem }
      )
    )
  } else {
    setToWatch(oldTime => [...oldTime, item])
  }
}
const removeTime = (item) => {
  const existingItem = toWatch.find(i => i.name === item.name)
  if (existingItem) {
    setToWatch(
      toWatch.map(oldItem =>
        oldItem === existingItem
          ? { ...oldItem }
          : { ...oldItem }
      )
    )
  } else {
    setToWatch(oldTime => [...oldTime, item])
  }
}



//const filteredData = movieData.filter(matchesFilterType)
const sortedData = movieData.sort(movieData.name)


  return (
    <div className="App">
      <div className="header">
        <h1>Your Curated Movie Playlist</h1>
      </div>
      {/* TODO: personalize your bakery (if you want) */}

      <div className="grid_container">
        <div className="movies_container">
          {sortedData.map((item, index) => (
            < Time data={item} id={index} onAddToWatch={addTime} />
          ))}
        </div>
        <div className="agg">
          <h2>Movies to Watch</h2>
          <p>{items}</p>
          <p>{totalTime} minutes of movie to watch!</p>
        </div>
      </div>
      <button onClick={() => setType([type, 'Horror'])}>Horror</button>
      <button onClick={() => setType([type, 'Horror'])}>SciFi</button>
      <button onClick={() => setType([type, 'Horror'])}>Horror</button>
      <button onClick={() => setType([type, 'Horror'])}>Horror</button>
      <h2>Current count: {type}</h2>
    </div>
    );
}



export default App;
