import './App.css';
import { useState } from "react";
import { Form } from "react-bootstrap";
import { movieData } from "./components/tiles.js"
import { Time } from './components/Time';
import { Filter } from './components/Filter';
import { FormControlLabel, Checkbox } from '@mui/material';


function App() {

  movieData.forEach((item) => {
    item.image = item.image;
  });

  const [toWatch, setToWatch] = useState([])
  const [type, setType] = useState("All")
  const [totalTime, setTime] = useState([])

  const selectFilterType = eventKey => {
    setType(eventKey);
  }    

  const matchesFilterType = item => {
    if(type === "All") { 
      return true
    } else if (item.genre.includes(type)) {
      return true
    } else {
      return false
    }
  }

  const items = toWatch.map(item =>
    <li key={item.id}>
      {item.name} : {item.time} minutes
    </li>

)

const updateTime  =  toWatch.reduce((partialSum, item) => partialSum + item.time, 0)  

const addTime = (item) => {
  const existingItem = toWatch.find(i => i.name === item.name)
  if (!existingItem) {
    setToWatch(oldTime => [...oldTime, item])
  } 
}
const removeTime = (item) => {
  const existingItem = toWatch.find(i => i.name === item.name)
  if (existingItem) {
    var index = toWatch.indexOf(existingItem)
    if(index >= 0) {
      toWatch.splice(index, 1);
    }
  } 
}

//const filteredData = movieData.filter(matchesFilterType)
const sortedData = movieData.sort(movieData.name)


  return (
    <div className="App">
      <div className="header">
        <h1>Your Curated Movie Playlist</h1>
        <div className="filterBar">
          <button>Remove Filters</button>
        </div>
      </div>
      {/* TODO: personalize your bakery (if you want) */}

      <div className="grid_container">
        <div className="movies_container">
          {movieData.map((item, index) => (
            < Time data={item} id={index} onAddToWatch={addTime} onRemoveToWatch={removeTime} updateTime={totalTime} />
          ))}
        </div>
        <div className="agg">
          <h2>Movies to Watch</h2>
          <p>{items}</p>
          <p>{totalTime} minutes of movie to watch!</p>
        </div>
      </div>
      <button onClick={() => setType([type, 'Horror'])}>Horror</button>
      <button onClick={() => setType([type, 'Horror'])}>SciFi</button>
      <button onClick={() => setType([type, 'Horror'])}>Horror</button>
      <button onClick={() => setType([type, 'Horror'])}>Horror</button>
      <h2>Current count: {type}</h2>
    </div>
    );
}



export default App;
